import React, { useState } from 'react'
import Carousel, { Modal, ModalGateway } from 'react-images'

import {
	Header,
	HeaderContent,
	HeaderTitle,
	GallerySection,
	GalleryContainer,
	GalleryHeader,
	GalleryTabs,
	Gallery,
	GalleryThumbnail,
} from './styledComponents'
import { PageWrapper } from 'components'
import { processedImages } from 'config/images'

export const OurStory = () => {
	const [selectedViewTab, setSelectedViewTab] = useState('Dating')
	const [lightBoxOpen, setLightBoxOpen] = useState(false)
	const [lightBoxIndex, setLightBoxIndex] = useState(0)

	return (
		<PageWrapper>
			<Header>
				<HeaderContent>
					<HeaderTitle>How it all started</HeaderTitle>
				</HeaderContent>
			</Header>
			<GallerySection>
				<GalleryContainer>
					<GalleryHeader>
						<GalleryTabs
							labelForKey='viewToggle'
							checkedValue={selectedViewTab}
							buttonArray={['How we met', 'Through the years', 'The Proposal']}
						/>
					</GalleryHeader>
					<Gallery>
						{processedImages.map((data, index) => (
							<GalleryThumbnail
								src={data.thumb}
								alt={`engagement ${index + 1}`}
								key={`engagement_${index + 1}`}
								onClick={() => {
									setLightBoxIndex(index)
									setLightBoxOpen(true)
								}}
							/>
						))}
					</Gallery>
				</GalleryContainer>
			</GallerySection>
			<ModalGateway>
				{lightBoxOpen ? (
					<Modal
						styles={{
							blanket: (base: { [key: string]: any }) => ({ ...base, zIndex: 1100 }),
							positioner: (base: { [key: string]: any }) => ({
								...base,
								zIndex: 1110,
							}),
							dialog: (base: { [key: string]: any }) => ({ ...base, zIndex: 1120 }),
						}}
						onClose={() => {
							setLightBoxIndex(0)
							setLightBoxOpen(!lightBoxOpen)
						}}
					>
						<Carousel
							views={processedImages.map((data) => ({
								src: data.full,
							}))}
							currentIndex={lightBoxIndex}
						/>
					</Modal>
				) : null}
			</ModalGateway>
		</PageWrapper>
	)
}
