import React, { useState } from 'react'

import { PageWrapper } from 'components'
import { processedImages } from 'config/images'

import {
	HeaderWrapper,
	HeaderSlider,
	HeaderSlide,
	HeaderCopyWrapper,
	HeaderTitle,
	HeaderSubtitle,
} from './styledComponents'

const headerSliderImages = [
	processedImages[53 - 1].full,
	processedImages[97 - 1].full,
	processedImages[80 - 1].full,
	processedImages[134 - 1].full,
	processedImages[26 - 1].full,
	processedImages[24 - 1].full,
	processedImages[33 - 1].full,
]

interface CountdownData {
	seconds: number
	minutes: number
	hours: number
	days: number
}
const countDownTo = (date: Date) => {
	const t = date.getTime() - new Date().getTime()

	if (t < 0) {
		return {
			seconds: 0,
			minutes: 0,
			hours: 0,
			days: 0,
		}
	}
	const seconds = Math.floor((t / 1000) % 60)
	const minutes = Math.floor((t / 1000 / 60) % 60)
	const hours = Math.floor((t / (1000 * 60 * 60)) % 24)
	const days = Math.floor(t / (1000 * 60 * 60 * 24))

	return {
		seconds,
		minutes,
		hours,
		days,
	}
}
const weddingStartDate = new Date('2020-07-18T16:00:00')

export const Home = () => {
	const [countDownString, setCountDownString] = useState<CountdownData>(
		countDownTo(weddingStartDate),
	)
	return (
		<PageWrapper>
			<HeaderWrapper>
				<HeaderSlider
					dots={true}
					infinite={true}
					speed={2500}
					autoplaySpeed={5000}
					slidesToShow={1}
					slidesToScroll={1}
					arrows={false}
					pauseOnHover={false}
					autoplay
					fade
				>
					{headerSliderImages.map((image, index) => (
						<HeaderSlide image={image} key={index} />
					))}
				</HeaderSlider>
				<HeaderCopyWrapper>
					<HeaderTitle>We're Engaged!</HeaderTitle>
					<HeaderSubtitle>Tying the knot July 18th 2020.</HeaderSubtitle>
				</HeaderCopyWrapper>
			</HeaderWrapper>
		</PageWrapper>
	)
}
