import React from 'react'

import { TETableButton } from 'components'

import { filterDateColumn, convertToDateTimeShort } from 'helpers'
import { RSVP } from 'interfaces'
import { Filter } from 'react-table'

interface Args {
	handleEditRSVP(message: RSVP): void
	handleDeleteRSVP(uid: string): void
}
export const tableColumns = ({ handleEditRSVP, handleDeleteRSVP }: Args) => [
	{
		id: 'dateCreated',
		Header: 'Date Created',
		filterMethod: (filter: Filter, rows: any[]) =>
			filterDateColumn(convertToDateTimeShort, filter, rows),
		accessor: 'date_created',
		Cell: (d: { value: number }) => convertToDateTimeShort(d.value),
	},
	{
		Header: 'Name',
		accessor: 'name',
	},
	{
		Header: 'Food Choice',
		accessor: 'foodChoice',
	},
	{
		Header: 'Comment',
		accessor: 'comment',
	},
	{
		id: 'status',
		Header: 'Status',
		accessor: (d: RSVP) => {
			switch (d.status) {
				case 0:
					return 'Submitted'
				case 1:
					return 'Recorded'
				default:
					return d.status
			}
		},
	},
	{
		id: 'actions',
		Header: '',
		accessor: 'uid',
		sortable: false,
		filterable: false,
		className: 'actionCell',
		width: 80,
		Cell: (d: { value: string; original: RSVP }) => (
			<>
				<TETableButton onClick={() => handleEditRSVP(d.original)} icon='edit' />
				<TETableButton onClick={() => handleDeleteRSVP(d.value)} icon='delete' />
			</>
		),
	},
]
