import styled from 'styled-components'

export const Button = styled.button`
	position: relative;
	display: block;
	width: 100%;
	font-size: 18px;
	padding: 5px 20px;
	text-align: center;
	cursor: ${(props) => (props.disabled ? 'not-allowed' : 'pointer')};
	border-radius: 5px;
	box-sizing: border-box;

	color: ${(props) => (props.disabled ? props.theme.gray : props.theme.darkerGray)};
	background-color: ${(props) => props.theme.white};
	border: 1px solid
		${(props) => (props.disabled ? props.theme.lighterGray : props.theme.lightGray)};

	transition: color 0.2s ease-in, background-color 0.2s ease-in, border 0.2s ease-in;
	:hover,
	:active {
		color: ${(props) => props.theme.white};
		background-color: ${(props) => props.theme.primary};
		border: 1px solid ${(props) => props.theme.primary};
	}
`
import React from 'react'

import { Button } from './styledComponents'

export interface TEButtonProps
	extends React.DetailedHTMLProps<
		React.ButtonHTMLAttributes<HTMLButtonElement>,
		HTMLButtonElement
	> {
	//TODO: Fix this. styled-components is weird with refs
	ref?:
		| ((instance: HTMLButtonElement | null) => void)
		| React.RefObject<HTMLButtonElement>
		| null
		| undefined
	// ref?: any
}
export const TEButton: React.FC<TEButtonProps> = React.forwardRef((props, ref) => {
	const { className = '', type = 'button', ...rest } = props
	return <Button className={`TEButton ${className}`} type={type} ref={ref} {...rest} />
})
import styled from 'styled-components'

export const Label = styled.label<{ disabled?: boolean }>`
	position: relative;
	display: block;
	width: auto;
	box-sizing: border-box;
	margin-bottom: 3px;
	font-size: 18px;
	color: ${(props) => (props.disabled ? props.theme.gray : props.theme.darkerGray)};

	@media (max-width: 550px) {
		font-size: 16px;
	}
	${(props) => {
		const { disabled, htmlFor } = props

		if (disabled) {
			return 'cursor: not-allowed;'
		}
		if (htmlFor) {
			return 'cursor: pointer;'
		}
		return 'cursor: default;'
	}}
`
import React from 'react'

import { Label } from './styledComponents'

export interface TELabelCustomProps {
	required?: boolean
	disabled?: boolean
}
export interface TELabelProps
	extends React.DetailedHTMLProps<React.LabelHTMLAttributes<HTMLLabelElement>, HTMLLabelElement>,
		TELabelCustomProps {
	ref?:
		| ((instance: HTMLLabelElement | null) => void)
		| React.RefObject<HTMLLabelElement>
		| null
		| undefined
}

export const TELabel: React.FC<TELabelProps> = React.forwardRef((props, ref) => {
	const { children, required, className = '', ...rest } = props
	return (
		<Label className={`TELabel ${className}`} ref={ref} {...rest}>
			{children}
			{required ? ' *' : ''}
		</Label>
	)
})
export const componentToHex = (component: number) => {
	// @ts-ignore
	const x = component.toString(16)
	return x.length === 1 ? '0' + x : x
}
export const hexToRgb = (hex: string) => {
	// Expand shorthand form (e.g. "03F") to full form (e.g. "0033FF")
	var shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i
	hex = hex.replace(shorthandRegex, function(_, r, g, b) {
		return r + r + g + g + b + b
	})

	var result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex)
	return result
		? {
				r: parseInt(result[1], 16),
				g: parseInt(result[2], 16),
				b: parseInt(result[3], 16),
		  }
		: null
}
export const colorBetweenColors = (color1: string, color2: string, percBetween: number) => {
	const color1RGB = hexToRgb(color1)
	const color2RGB = hexToRgb(color2)

	if (!color1RGB || !color2RGB) {
		throw new Error('Invalid colors supplied')
	}
	const r = Math.ceil(color1RGB.r * percBetween + color2RGB.r * (1 - percBetween))
	const g = Math.ceil(color1RGB.g * percBetween + color2RGB.g * (1 - percBetween))
	const b = Math.ceil(color1RGB.b * percBetween + color2RGB.b * (1 - percBetween))

	return '#' + componentToHex(r) + componentToHex(g) + componentToHex(b)
}
interface ButtonData {
	label: string
	value?: string
	key?: string
}
interface ManipulateRowData {
	rowData: string | ButtonData
	labelForKey: string
}
export const manipulateRowData = ({ rowData, labelForKey = '' }: ManipulateRowData) => {
	//Allowing For Greater Shorthand
	if (typeof rowData === 'string') {
		const label = rowData
		const value = rowData
		const key = labelForKey + rowData
		return { label, value, key }
	} else if (typeof rowData === 'object') {
		let { label, value, key } = rowData

		if (!value) {
			value = label
		}
		if (!key) {
			key = labelForKey + label
		}

		return { label, value, key }
	}
	return rowData
}
