import React, { useState } from 'react'
import Carousel, { Modal, ModalGateway } from 'react-images'
import Pagination from 'rc-pagination'

import {
	Header,
	HeaderContent,
	HeaderTitle,
	GallerySection,
	GalleryContainer,
	GalleryHeader,
	GalleryTabs,
	Gallery,
	GalleryThumbnail,
} from './styledComponents'
import { PageWrapper } from 'components'
import { processedImages } from 'config/images'

const pageSize = 12
const totalPages = Math.ceil(processedImages.length / pageSize)
console.log(totalPages)
export const OurStory = () => {
	const [selectedViewTab, setSelectedViewTab] = useState('How We Met')
	const [lightBoxOpen, setLightBoxOpen] = useState(false)
	const [lightBoxIndex, setLightBoxIndex] = useState(0)
	const [currentPage, setCurrentPage] = useState(1)

	return (
		<PageWrapper>
			<Header>
				<HeaderContent>
					<HeaderTitle>How it all started</HeaderTitle>
				</HeaderContent>
			</Header>
			<GallerySection>
				<GalleryContainer>
					<GalleryHeader>
						<GalleryTabs
							labelForKey='viewToggle'
							checkedValue={selectedViewTab}
							buttonArray={['How We Met', 'Through The Years', 'The Proposal']}
							onChange={(e) => setSelectedViewTab(e.target.value)}
						/>
					</GalleryHeader>
					<Gallery>
						{processedImages
							.slice(currentPage * pageSize - 1, (currentPage + 1) * pageSize - 1)
							.map((data, index) => (
								<GalleryThumbnail
									src={data.thumb}
									alt={`engagement ${index + 1}`}
									key={`engagement_${index + 1}`}
									onClick={() => {
										setLightBoxIndex(index)
										setLightBoxOpen(true)
									}}
								/>
							))}
					</Gallery>
					<Pagination
						onChange={(page) => setCurrentPage(page)}
						current={currentPage}
						total={totalPages}
					/>
				</GalleryContainer>
			</GallerySection>
			<ModalGateway>
				{lightBoxOpen ? (
					<Modal
						styles={{
							blanket: (base: { [key: string]: any }) => ({ ...base, zIndex: 1100 }),
							positioner: (base: { [key: string]: any }) => ({
								...base,
								zIndex: 1110,
							}),
							dialog: (base: { [key: string]: any }) => ({ ...base, zIndex: 1120 }),
						}}
						onClose={() => {
							setLightBoxIndex(0)
							setLightBoxOpen(!lightBoxOpen)
						}}
					>
						<Carousel
							views={processedImages.map((data) => ({
								src: data.full,
							}))}
							currentIndex={lightBoxIndex}
						/>
					</Modal>
				) : null}
			</ModalGateway>
		</PageWrapper>
	)
}
