import React, { useState } from 'react'
import Carousel, { Modal, ModalGateway } from 'react-images'

import {
	Header,
	HeaderContent,
	HeaderTitle,
	GallerySection,
	GalleryContainer,
	Gallery,
	GalleryThumbnail,
} from './styledComponents'
import { PageWrapper } from 'components'
import { processedImages } from 'config/images'

export const OurStory = () => {
	const [lightBoxOpen, setLightBoxOpen] = useState(true)
	const [lightBoxIndex, setLightBoxIndex] = useState(0)

	return (
		<PageWrapper>
			<Header>
				<HeaderContent>
					<HeaderTitle>How it all started</HeaderTitle>
				</HeaderContent>
			</Header>
			<GallerySection>
				<GalleryContainer>
					<Gallery>
						{processedImages.map((data, index) => (
							<GalleryThumbnail
								src={data.thumb}
								alt={`engagement ${index + 1}`}
								key={`engagement_${index + 1}`}
								onClick={() => setLightBoxIndex(index)}
							/>
						))}
					</Gallery>
				</GalleryContainer>
			</GallerySection>
			<ModalGateway>
				{lightBoxOpen ? (
					<Modal onClose={() => setLightBoxOpen(!lightBoxOpen)}>
						<Carousel
							views={processedImages.map((data) => ({
								src: data.full,
							}))}
							currentIndex={lightBoxIndex}
						/>
					</Modal>
				) : null}
			</ModalGateway>
		</PageWrapper>
	)
}
