import styled from 'styled-components'

import { pageHeaders } from 'config/images'

export const Header = styled.div`
	height: 60vh;
	min-height: 400px;
	background-image: url(${pageHeaders.registry});
	background-size: cover;
	background-repeat: no-repeat;
	background-position: center;
	display: flex;
	justify-content: center;
	align-items: center;
`
export const HeaderContent = styled.div``
export const HeaderTitle = styled.h1`
	margin-top: 0px;
	margin-bottom: 0px;
	font-size: 5vw;
	color: #ffffff;
	line-height: 1.1;
	text-align: center;
	text-shadow: 1px 1px 1px rgba(0, 0, 0, 0.2);
	max-width: 60vw;

	@media only screen and (max-width: 800px) {
		font-size: 10vw;
	}
	@media only screen and (max-width: 500px) {
		font-size: 44px;
	}
`

export const DonationSection = styled.div`
	padding-top: 50px;
	padding-bottom: 30px;
`
export const DonationContainer = styled.div`
	max-width: 900px;
	margin: 0px auto;
	padding-left: 30px;
	padding-right: 30px;
`
export const DonationCopy = styled.p`
	margin-top: 0px;
	margin-bottom: 10px;
	line-height: 1.5;
	font-size: 20px;
	color: ${(props) => props.theme.darkerGray};
	text-align: center;
	margin-bottom: 50px;

	@media only screen and (max-width: 800px) {
		font-size: 18px;
	}
	@media only screen and (max-width: 600px) {
		font-size: 16px;
	}
`
export const DonationFormWrapper = styled.div`
	text-align: center;
	padding: 0px 30px;
	max-width: 650px;
	margin-left: auto;
	margin-right: auto;
`
export const DonationForm = styled.form``
export const DonationStripeWrapper = styled.div`
	padding: 8px 10px;
	border: 1px solid ${(props) => props.theme.lightGray};
	border-radius: 5px;
	margin-bottom: 10px;
`

export const GiftRegistrySection = styled.div`
	padding-top: 30px;
	padding-bottom: 50px;
`
export const GiftRegistryContainer = styled.div`
	max-width: 900px;
	margin: 0px auto;
	padding-left: 30px;
	padding-right: 30px;
`
export const GiftRegistryTitle = styled.h2`
	margin-top: 0px;
	margin-bottom: 10px;
	font-size: 46px;
	line-height: 1.2;
	color: ${(props) => props.theme.primary};
	text-align: center;

	@media only screen and (max-width: 1000px) {
		font-size: 38px;
	}
	@media only screen and (max-width: 800px) {
		font-size: 32px;
	}
	@media only screen and (max-width: 700px) {
		text-align: center;
		font-size: 36px;
	}
	@media only screen and (max-width: 450px) {
		font-size: 26px;
	}
`
export const GiftRegistryCopy = styled.p`
	margin-top: 0px;
	margin-bottom: 10px;
	line-height: 1.5;
	font-size: 20px;
	color: ${(props) => props.theme.darkerGray};
	text-align: center;

	@media only screen and (max-width: 800px) {
		font-size: 18px;
	}
	@media only screen and (max-width: 600px) {
		font-size: 16px;
	}
`
export const GiftRegistryLinkWrapper = styled.div`
	display: flex;
	justify-content: center;
	align-items: center;
`
export const GiftRegisteryLink = styled.a`
	display: block;
	width: 50%;
	margin: 30px;
	max-width: 300px;
	transition: opacity 0.2s ease-in;
	cursor: pointer;

	&:hover {
		opacity: 0.5;
	}
`
export const GiftRegisteryLogo = styled.img`
	display: block;
	width: 100%;
`
