import React, { useState } from 'react'
import Carousel, { Modal, ModalGateway } from 'react-images'

import {
	Header,
	HeaderContent,
	HeaderTitle,
	GallerySection,
	GalleryContainer,
	Gallery,
	GalleryThumbnail,
} from './styledComponents'
import { PageWrapper } from 'components'
import { processedImages } from 'config/images'

export const OurStory = () => {
	const [lightBoxOpen, setLightBoxOpen] = useState(false)
	const [lightBoxIndex, setLightBoxIndex] = useState(0)
	console.log(processedImages)
	console.log(processedImages.map((d) => d.thumb))
	console.log(processedImages.map((d) => d.full))
	return (
		<PageWrapper>
			<Header>
				<HeaderContent>
					<HeaderTitle>How it all started</HeaderTitle>
				</HeaderContent>
			</Header>
			<GallerySection>
				<GalleryContainer>
					<Gallery>
						{processedImages.map((data, index) => (
							<GalleryThumbnail
								src={data.thumb}
								alt={`engagement ${index + 1}`}
								key={`engagement_${index + 1}`}
								onClick={() => {
									setLightBoxIndex(index)
									setLightBoxOpen(true)
								}}
							/>
						))}
					</Gallery>
				</GalleryContainer>
			</GallerySection>
			<ModalGateway
				styles={{
					view: () => ({ zIndex: 10000, background: 'red', border: '1px solid red' }),
				}}
			>
				{lightBoxOpen ? (
					<Modal
						styles={{
							view: () => ({
								zIndex: 10000,
								background: 'red',
								border: '1px solid red',
							}),
						}}
						onClose={() => {
							setLightBoxIndex(0)
							setLightBoxOpen(!lightBoxOpen)
						}}
					>
						<Carousel
							views={processedImages.map((data) => ({
								src: data.full,
							}))}
							currentIndex={lightBoxIndex}
						/>
					</Modal>
				) : null}
			</ModalGateway>
		</PageWrapper>
	)
}
